import React, { useState } from 'react';

const Todos = () => {
  const [formData, setFormData] = useState({
    open: '',
    high: '',
    low: '',
    volume: ''
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPrediction(null);

    try {
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      const data = await response.json();
      setPrediction(data.prediction); // Expecting { prediction: ... } from Flask
    } catch (error) {
      console.error("Error fetching prediction:", error);
      setPrediction("Error fetching prediction");
    }

    setLoading(false);
  };

  return (
    <div className="container mt-4">
      {/* Card */}
      <div className="card mb-4" style={{ width: '18rem' }}>
        <img
          src="https://images.unsplash.com/photo-1520209759809-a9bcb6cb3241?fm=jpg&q=60&w=3000&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8aW1nfGVufDB8fDB8fHww"
          className="card-img-top"
          alt="blank"
        />
        <div className="card-body">
          <h5 className="card-title">Bitcoin Prediction</h5>
          <p className="card-text">
            Enter Bitcoin market values to predict the price trend using AI.
          </p>
        </div>
      </div>

      {/* Form */}
      <form onSubmit={handleSubmit} className="p-3 border rounded bg-light" style={{ width: '18rem' }}>
        <div className="mb-3">
          <label className="form-label">Open</label>
          <input
            type="number"
            className="form-control"
            name="open"
            value={formData.open}
            onChange={handleChange}
            placeholder="Enter open price"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">High</label>
          <input
            type="number"
            className="form-control"
            name="high"
            value={formData.high}
            onChange={handleChange}
            placeholder="Enter high price"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Low</label>
          <input
            type="number"
            className="form-control"
            name="low"
            value={formData.low}
            onChange={handleChange}
            placeholder="Enter low price"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Volume</label>
          <input
            type="number"
            className="form-control"
            name="volume"
            value={formData.volume}
            onChange={handleChange}
            placeholder="Enter volume"
            required
          />
        </div>

        <button type="submit" className="btn btn-primary w-100">
          {loading ? "Predicting..." : "Predict"}
        </button>
      </form>

      {/* Prediction Result */}
      {prediction !== null && (
        <div className="alert alert-info mt-3" style={{ width: '18rem' }}>
          <strong>Prediction Result:</strong> {prediction}
        </div>
      )}
    </div>
  );
};

export default Todos;
