import React from 'react';

const MeanPredictionCard = ({ predictions }) => {
  const validPredictions = predictions.filter(p => typeof p === 'number' && !isNaN(p));
  const mean =
    validPredictions.length > 0
      ? (validPredictions.reduce((a, b) => a + b, 0) / validPredictions.length).toFixed(2)
      : null;

  return (
    <div className="card shadow-sm border-0 mb-3" style={{ borderRadius: '1rem', maxWidth: '300px', width: '100%' }}>
      <div className="card-body p-3 text-center">
        <img
          src="https://www.google.com/imgres?q=cryptocurrency%20bitcoin%20logo&imgurl=https%3A%2F%2Fwww.shutterstock.com%2Fimage-vector%2Fcrypto-currency-golden-coin-black-600nw-593193626.jpg&imgrefurl=https%3A%2F%2Fwww.shutterstock.com%2Fsearch%2Fbitcoin-logo-transparent&docid=fh9373S6d_0LtM&tbnid=ZjYfaqd6BnDZVM&vet=12ahUKEwizyszY1f2OAxXFxzgGHbp8GJcQM3oECBcQAA..i&w=600&h=600&hcb=2&ved=2ahUKEwizyszY1f2OAxXFxzgGHbp8GJcQM3oECBcQAA"
          alt="Bitcoin"
          style={{ width: '36px', marginBottom: '0.5rem' }}
        />
        <h6 className="fw-bold mb-2" style={{ color: '#636e72', fontSize: '1rem' }}>Mean of Predictions</h6>
        {mean !== null ? (
          <div className="alert alert-info py-1" style={{ fontSize: '1.1rem', fontWeight: 'bold' }}>
            ðŸ“Š Mean: {mean}
          </div>
        ) : (
          <div className="text-muted" style={{ fontSize: '0.95rem' }}>
            Enter values and predict in all cards to see the mean.
          </div>
        )}
      </div>
    </div>
  );
};

export default MeanPredictionCard;
